// Normalizar estructura del webhook
const input = $input.first().json;

// Algunos webhooks vienen con .body, otros directamente
const data = input.body || input;

return [{
  json: data
}];

// Obtener todos los jobs
const jobs = $input.all();

console.log('üìä Total jobs received:', jobs.length);

// Log de todos los jobs para debugging
jobs.forEach(j => {
  console.log(`  - Job #${j.json.id}: ${j.json.name} | Status: ${j.json.status} | Stage: ${j.json.stage}`);
});

// Filtrar SOLO jobs con status 'failed'
const failedJobs = jobs.filter(job => job.json.status === 'failed');

console.log(`\nüîç Jobs with status 'failed': ${failedJobs.length}`);

if (failedJobs.length === 0) {
  console.error('‚ùå NO FAILED JOBS FOUND');
  throw new Error('No failed jobs found in pipeline. All jobs either passed, were skipped, or are still running.');
}

// Ordenar por ID descendente (m√°s reciente primero)
failedJobs.sort((a, b) => b.json.id - a.json.id);

const selectedJob = failedJobs[0].json;
console.log(`\n‚úÖ SELECTED JOB:`);
console.log(`   ID: ${selectedJob.id}`);
console.log(`   Name: ${selectedJob.name}`);
console.log(`   Status: ${selectedJob.status}`);
console.log(`   Stage: ${selectedJob.stage}`);
console.log(`   Failure Reason: ${selectedJob.failure_reason || 'Not specified'}`);

return [{ json: selectedJob }];

// --- 1Ô∏è‚É£ Obtener salida de Ollama ---
const input = $input.first().json;
const ollamaResponse = Array.isArray(input)
  ? input[0]
  : input.body?.[0] || input;

const analysis = ollamaResponse.response || ollamaResponse.output || "(sin respuesta del modelo)";

// --- 2Ô∏è‚É£ Obtener datos del pipeline (Uso seguro de $items) ---
// Usamos el operador ternario para asegurar que si $items falla, webhookData sea un objeto vac√≠o {}
const webhookData = $items('Normalize Data').length > 0
    ? $items('Normalize Data')[0].json 
    : {}; // <--- Esto evita el ReferenceError

// --- 3Ô∏è‚É£ Obtener datos del job fallido (Uso seguro de $items) ---
const failedJobData = $items('Get Failed Job').length > 0
    ? $items('Get Failed Job')[0].json 
    : {}; // <--- Esto evita el ReferenceError


// --- 4Ô∏è‚É£ Construir el mensaje de texto formateado (Opci√≥n 1 del paso anterior) ---
const messageText = `
*üö® Pipeline Fallido en GitLab*

*üì¶ Proyecto:* ${webhookData.project?.name || 'N/A'}
*üåø Rama:* ${webhookData.object_attributes?.ref || 'N/A'}
*üß© Job Fallido:* ${failedJobData.name || 'N/A'} (${failedJobData.stage || 'N/A'})
*‚è±Ô∏è Duraci√≥n:* ${failedJobData.duration ? failedJobData.duration + 's' : 'N/A'}
*üîó Enlaces:* <${webhookData.object_attributes?.url || '#'}|Ver Pipeline> ‚Ä¢ <${failedJobData.web_url || '#'}|Ver Job>

---
*ü§ñ An√°lisis AI*
${analysis}
`;


// --- 5Ô∏è‚É£ Formatear salida ---
return [{
  json: {
    success: true,
    pipeline: {
      id: webhookData.object_attributes?.id,
      project: webhookData.project?.name,
      branch: webhookData.object_attributes?.ref,
      commit: webhookData.object_attributes?.sha?.substring(0, 8),
      url: webhookData.object_attributes?.url,
      status: webhookData.object_attributes?.status
    },
    failed_job: {
      id: failedJobData.id,
      name: failedJobData.name,
      stage: failedJobData.stage,
      url: failedJobData.web_url,
      duration: failedJobData.duration
    },
    ai_analysis: analysis,
    timestamp: new Date().toISOString(),
    // Usamos el texto para el mensaje de Slack (como decidimos)
    slack_text_content: messageText 
  }
}];

const data = $input.first().json;

const successMessage = `
*‚úÖ Pipeline Exitoso en GitLab*

*üì¶ Proyecto:* ${data.project?.name || 'N/A'}
*üåø Rama:* ${data.object_attributes?.ref || 'N/A'}
*‚è±Ô∏è Duraci√≥n:* ${data.object_attributes?.duration || 'N/A'}s
*üîó Ver Pipeline:* ${data.object_attributes?.url || '#'}

*üìä Jobs completados:*
${data.builds?.filter(b => b.status === 'success').map(b => `  ‚Ä¢ ${b.name} (${b.duration}s)`).join('\n') || 'N/A'}
`;

return [{
  json: {
    slack_text_content: successMessage
  }
}];

// Ignorar eventos de pipeline todav√≠a en ejecuci√≥n
const data = $input.first().json;

return [{
  json: {
    ignored: true,
    message: "Pipeline still running, ignoring event",
    status: data.object_attributes.status,
    pipeline_id: data.object_attributes.id
  }
}];

// Validar que hay datos antes de procesar
const input = $json;

// Si no hay data, es porque el job no tiene logs
if (!input.data || input.data.trim() === '') {
  console.log('‚ö†Ô∏è No logs available for this job');
  return [{
    json: {
      body: {
        model: "mistral:7b-instruct",
        prompt: "El job fall√≥ pero no tiene logs disponibles. Esto puede ocurrir si el job fue cancelado o no se ejecut√≥.",
        stream: false,
        options: { temperature: 0.2, top_p: 0.9 }
      }
    }
  }];
}

// Limpia caracteres ANSI del log (colores de terminal)
const cleanLog = input.data.replace(/\u001b\[[0-9;]*m/g, '');

// Crea el cuerpo JSON que Ollama espera
const body = {
  model: "mistral:7b-instruct",
  prompt: `Eres un experto en Python y CI/CD. Analiza este error de pipeline y proporciona:

üìã RESUMEN (1-2 l√≠neas): ¬øQu√© fall√≥?

üîç CAUSA RA√çZ: ¬øPor qu√© fall√≥ exactamente?

üìÇ ARCHIVO(S) AFECTADO(S): Lista los archivos y l√≠neas espec√≠ficas con errores

‚úÖ SOLUCI√ìN: Proporciona el c√≥digo completo corregido del archivo afectado

Log del error:
---
${cleanLog}
---
Responde en formato claro, estructurado y profesional.`,
  stream: false,
  options: { temperature: 0.2, top_p: 0.9 }
};

return [{ json: { body } }];
