const input = $input.first().json;
const data = input.body || input;
return [{ json: data }];

const jobs = $input.all();
const failedJobs = jobs.filter(job => job.json.status === 'failed');
if (failedJobs.length === 0) {
  throw new Error('No failed jobs found');
}
failedJobs.sort((a, b) => b.json.id - a.json.id);
return [{ json: failedJobs[0].json }];

const input = $json;
if (!input.data || input.data.trim() === '') {
  return [{
    json: {
      body: {
        model: "mistral:7b-instruct",
        prompt: "El job fall√≥ pero no tiene logs disponibles.",
        stream: false,
        options: { temperature: 0.2, top_p: 0.9 }
      }
    }
  }];
}
const cleanLog = input.data.replace(/\u001b\[[0-9;]*m/g, '');
const body = {
  model: "mistral:7b-instruct",
  prompt: `Analiza este error de pipeline de Python/CI/CD y proporciona:\n\nüìã RESUMEN (1-2 l√≠neas): ¬øQu√© fall√≥?\n\nüîç CAUSA RA√çZ: ¬øPor qu√©?\n\nüìÇ ARCHIVO(S): Lista archivos y l√≠neas\n\n‚úÖ SOLUCI√ìN: C√≥digo corregido\n\nLog:\n---\n${cleanLog}\n---`,
  stream: false,
  options: { temperature: 0.2, top_p: 0.9 }
};
return [{ json: { body } }];

const input = $input.first().json;
